; ---------------------------------------------------------------
; void __near__ drawSprite (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_drawSprite: near

.segment	"CODE"

;
; oam_clear(); // Clears all sprites from the sprite buffer.
;
	jsr     _oam_clear
;
; oam_meta_spr(testSpriteData.X, testSpriteData.Y, testSprite); // Draws the metasprite at x pos 64, y pos 80 and using the testSprite data. Nes Screen is 256 x 240 in pixels, so max range for sprite drawing is 255, 239.
;
	jsr     decsp2
	lda     _testSpriteData
	ldy     #$01
	sta     (sp),y
	lda     _testSpriteData+1
	dey
	sta     (sp),y
	lda     #<(_testSprite)
	ldx     #>(_testSprite)
	jsr     _oam_meta_spr
;
; oam_meta_spr(portal1SpriteData.X, portal1SpriteData.Y, testPortal1);
;
	jsr     decsp2
	lda     _portal1SpriteData
	ldy     #$01
	sta     (sp),y
	lda     _portal1SpriteData+1
	dey
	sta     (sp),y
	lda     #<(_testPortal1)
	ldx     #>(_testPortal1)
	jsr     _oam_meta_spr
;
; oam_meta_spr(portal2SpriteData.X, portal2SpriteData.Y, testPortal2);
;
	jsr     decsp2
	lda     _portal2SpriteData
	ldy     #$01
	sta     (sp),y
	lda     _portal2SpriteData+1
	dey
	sta     (sp),y
	lda     #<(_testPortal2)
	ldx     #>(_testPortal2)
	jmp     _oam_meta_spr

.endproc

; ---------------------------------------------------------------
; void __near__ movement (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_movement: near

.segment	"CODE"

;
; unsigned char oldX = testSpriteData.X;
;
	lda     _testSpriteData
	jsr     pusha
;
; unsigned char oldY = testSpriteData.Y;
;
	lda     _testSpriteData+1
	jsr     pusha
;
; if(pad1 & PAD_LEFT){ // If Left on the DPAD is pressed, remove one from the player's X data.
;
	lda     _pad1
	and     #$02
	beq     L0008
;
; testSpriteData.X -= 1;
;
	dec     _testSpriteData
;
; else if (pad1 & PAD_RIGHT){ // If Right on the DPAD is pressed, add one to the player's X data.
;
	jmp     L000F
L0008:	lda     _pad1
	and     #$01
	beq     L0006
;
; testSpriteData.X += 1;
;
	inc     _testSpriteData
;
; if (playerWallCollision(&testSpriteData))
;
L000F:	lda     #<(_testSpriteData)
	ldx     #>(_testSpriteData)
	jsr     _playerWallCollision
	tax
	beq     L0006
;
; testSpriteData.X = oldX;
;
	ldy     #$01
	lda     (sp),y
	sta     _testSpriteData
;
; }
;
L0006:	jmp     incsp2

.endproc

; ---------------------------------------------------------------
; void __near__ portalPlayerCollision (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_portalPlayerCollision: near

.segment	"CODE"

;
; portal1Collision = check_collision(&testSpriteData, &portal1SpriteData); // Checks if the player is colliding with the data for the first portal.
;
	lda     #<(_testSpriteData)
	ldx     #>(_testSpriteData)
	jsr     pushax
	lda     #<(_portal1SpriteData)
	ldx     #>(_portal1SpriteData)
	jsr     _check_collision
	sta     _portal1Collision
;
; portal2Collision = check_collision(&testSpriteData, &portal2SpriteData); // Checks if the player is colliding with the data for the second portal.
;
	lda     #<(_testSpriteData)
	ldx     #>(_testSpriteData)
	jsr     pushax
	lda     #<(_portal2SpriteData)
	ldx     #>(_portal2SpriteData)
	jsr     _check_collision
	sta     _portal2Collision
;
; if (portal1Collision) // If the player is colliding with the first portal.
;
	lda     _portal1Collision
	beq     L0002
;
; testSpriteData.X = portal2SpriteData.X; // Sets the player X data to the X location of the second portal.
;
	lda     _portal2SpriteData
	sta     _testSpriteData
;
; testSpriteData.Y = portal2SpriteData.Y; // Sets the player Y data to the Y location of the second portal.
;
	lda     _portal2SpriteData+1
	sta     _testSpriteData+1
;
; else if (portal2Collision) // If the player is colliding with the second portal.
;
	rts
;
; }
;
L0002:	rts

.endproc

; ---------------------------------------------------------------
; unsigned char __near__ wallDetection (unsigned char x, unsigned char y)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_wallDetection: near

.segment	"CODE"

;
; {
;
	jsr     pusha
;
; if (x >= 32 || y >= 30)
;
	ldy     #$01
	lda     (sp),y
	cmp     #$20
	bcs     L0006
	dey
	lda     (sp),y
	cmp     #$1E
	bcs     L0006
	ldx     #$00
	jmp     L0008
;
; return 1;
;
L0006:	ldx     #$00
	lda     #$01
	jmp     incsp2
;
; return testlevelcollision[y * 32 + x];
;
L0008:	lda     (sp),y
	jsr     shlax4
	stx     tmp1
	asl     a
	rol     tmp1
	sta     ptr1
	iny
	lda     (sp),y
	clc
	adc     ptr1
	ldx     tmp1
	bcc     L0005
	inx
L0005:	sta     ptr1
	txa
	clc
	adc     #>(_testlevelcollision)
	sta     ptr1+1
	ldy     #<(_testlevelcollision)
	ldx     #$00
	lda     (ptr1),y
;
; }
;
	jmp     incsp2

.endproc

; ---------------------------------------------------------------
; unsigned char __near__ playerWallCollision (struct spriteData *spr)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_playerWallCollision: near

.segment	"CODE"

;
; {
;
	jsr     pushax
;
; unsigned char leftTile   = spr->X >> 3;
;
	ldy     #$01
	lda     (sp),y
	sta     ptr1+1
	dey
	lda     (sp),y
	sta     ptr1
	lda     (ptr1),y
	lsr     a
	lsr     a
	lsr     a
	jsr     pusha
;
; unsigned char rightTile  = (spr->X + spr->width) >> 3;
;
	ldy     #$02
	lda     (sp),y
	sta     ptr1+1
	dey
	lda     (sp),y
	sta     ptr1
	dey
	lda     (ptr1),y
	sta     sreg
	ldy     #$02
	lda     (sp),y
	sta     ptr1+1
	dey
	lda     (sp),y
	sta     ptr1
	ldy     #$03
	lda     (ptr1),y
	clc
	adc     sreg
	ror     a
	lsr     a
	lsr     a
	jsr     pusha
;
; unsigned char topTile    = spr->Y >> 3;
;
	ldy     #$03
	lda     (sp),y
	sta     ptr1+1
	dey
	lda     (sp),y
	sta     ptr1
	dey
	lda     (ptr1),y
	lsr     a
	lsr     a
	lsr     a
	jsr     pusha
;
; unsigned char bottomTile = (spr->Y + spr->height) >> 3;
;
	ldy     #$04
	lda     (sp),y
	sta     ptr1+1
	dey
	lda     (sp),y
	sta     ptr1
	ldy     #$01
	lda     (ptr1),y
	sta     sreg
	ldy     #$04
	lda     (sp),y
	sta     ptr1+1
	dey
	lda     (sp),y
	sta     ptr1
	dey
	lda     (ptr1),y
	clc
	adc     sreg
	ror     a
	lsr     a
	lsr     a
	jsr     pusha
;
; if (wallDetection(leftTile, topTile)  ||
;
	ldy     #$03
	lda     (sp),y
	jsr     pusha
	ldy     #$02
	lda     (sp),y
	jsr     _wallDetection
	tax
	bne     L0003
;
; wallDetection(rightTile, topTile) ||
;
	ldy     #$02
	lda     (sp),y
	jsr     pusha
	ldy     #$02
	lda     (sp),y
	jsr     _wallDetection
	tax
	bne     L0003
;
; wallDetection(leftTile, bottomTile) ||
;
	ldy     #$03
	lda     (sp),y
	jsr     pusha
	ldy     #$01
	lda     (sp),y
	jsr     _wallDetection
	tax
	bne     L0003
;
; wallDetection(rightTile, bottomTile))
;
	ldy     #$02
	lda     (sp),y
	jsr     pusha
	ldy     #$01
	lda     (sp),y
	jsr     _wallDetection
	tax
	beq     L0005
;
; return 1; // collision
;
L0003:	ldx     #$00
	lda     #$01
;
; }
;
L0005:	jmp     incsp6

.endproc

; ---------------------------------------------------------------
; void __near__ main (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_main: near

.segment	"CODE"

;
; ppu_off(); // Turns the screen off.
;
	jsr     _ppu_off
;
; pal_bg(paletteBackground); // Sets the Background Palette.
;
	lda     #<(_paletteBackground)
	ldx     #>(_paletteBackground)
	jsr     _pal_bg
;
; pal_spr(paletteSprite); // Sets the Sprite Palette.
;
	lda     #<(_paletteSprite)
	ldx     #>(_paletteSprite)
	jsr     _pal_spr
;
; set_scroll_y(0xff);
;
	ldx     #$00
	lda     #$FF
	jsr     _set_scroll_y
;
; vram_adr(NAMETABLE_A); // Sets the address to NameTable A (the first name table), for the data to be written to.
;
	ldx     #$20
	lda     #$00
	jsr     _vram_adr
;
; vram_write(testlevel, sizeof(testlevel)); // Writes the data from the testlevel header 
;
	lda     #<(_testlevel)
	ldx     #>(_testlevel)
	jsr     pushax
	ldx     #$04
	lda     #$00
	jsr     _vram_write
;
; bank_spr(1); // Tells the program to use the second batch of tiles from the bank for the sprite. Both background and sprite uses 0 by default, however Alpha3 has the sprite tiles on 2.
;
	lda     #$01
	jsr     _bank_spr
;
; ppu_on_all(); // Turns on the Screen.
;
	jsr     _ppu_on_all
;
; ppu_wait_nmi();
;
L0002:	jsr     _ppu_wait_nmi
;
; pad1 = pad_poll(0);  // read the first controller
;
	lda     #$00
	jsr     _pad_poll
	sta     _pad1
;
; drawSprite(); // Draw all sprites.
;
	jsr     _drawSprite
;
; movement(); // Handle Player Movement.
;
	jsr     _movement
;
; portalPlayerCollision(); // Handle Portal Collision with the Player.
;
	jsr     _portalPlayerCollision
;
; while (1){
;
	jmp     L0002

.endproc

