L0008:	lda     (sp),y
	jsr     shlax4
	stx     tmp1
	asl     a
	rol     tmp1
	sta     ptr1
	iny
	lda     (sp),y
	clc
	adc     ptr1
	ldx     tmp1
	bcc     L0005
	inx
L0005:	sta     ptr1
	txa
	clc
	adc     #>(_testlevelcollision)
	sta     ptr1+1
	ldy     #<(_testlevelcollision)
	ldx     #$00
	lda     (ptr1),y
;
; }
;
	jmp     incsp2

.endproc

